import React from "react"
import { Modal, Form, Icon, Input, Button, Spin } from 'antd';
import { connect } from "react-redux"
import clientActions from "../../../redux/clients/actions"
import styles from "./styles.module.scss"


class ClientModal extends React.Component {

  handleCancel = e =>{
    e.preventDefault()
    this.props.dispatch({
      type:clientActions.SET_STATE,
      payload: {showAddModal: false}
    })
  }
  render(){
    const { showAddModal} = this.props
    const { getFieldDecorator } = this.props.form;
    const {loading, showErrorMessage, message} = this.props
    return (
      <Modal
        title="Basic Modal"
        visible={showAddModal}
        width={700}
        onOk={this.handleOk}
        onCancel={this.handleCancel}
        footer={null}
        destroyOnClose={true}
      >
        <Spin spinning={loading} tip="Loading...">
          <Form onSubmit={this.handleSubmit} className="login-form">
            <Form.Item label="Email">
              {getFieldDecorator('email', {
                rules: [{ required: true, message: 'Email Obligatoire!' }],
              })(
                <Input
                  prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                />,
              )}
            </Form.Item>
            <Form.Item label="Mot de passe">
              {getFieldDecorator('password', {
                rules: [{ required: true, message: 'Mot de passe Obligatoire!' }],
              })(
                <Input
                  prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                  type="password"
                />,
              )}
            </Form.Item>

            <Button
              htmlType="submit"
              variant="contained"
              color="primary"
              className={styles.submit}
            >
              Se connecter
            </Button>

          </Form>
        </Spin>
      </Modal>
    )
  }
}

const mapStateToProps = state=>{
  return state.clients
}

const WrappedClientModalForm = Form.create({ name: 'add_client_modal' })(ClientModal);

export default connect(mapStateToProps)(WrappedClientModalForm)
