import React from "react"
import { Modal, Form, Icon, Input, Button, Spin, Alert, Select } from 'antd';
import { connect } from "react-redux"
import purchaseActions from "../../../redux/purchase/actions"
import clientsActions from "../../../redux/clients/actions"
import { IdcardOutlined, PhoneOutlined } from '@ant-design/icons';
import styles from "./styles.module.scss"

const { Option } = Select;

class ClientModal extends React.Component {
  state = {
    data: [],
    value: undefined,
  }

  handleCancel = e =>{
    e.preventDefault()
    this.props.dispatch({
      type:purchaseActions.SET_STATE,
      payload: {showAddModal: false}
    })
  }

  handleSubmit = e=>{
    e.preventDefault()
    const { form, dispatch } = this.props
    form.validateFields((error, values) => {
      if(!error){
        dispatch({
          type: purchaseActions.ADD_NEW_CLIENT,
          payload: {newClient:{...values}}
        })
      }
    })
  }

  searchForClients = search=>{
    const {size, page, sort, order} = this.props.clients
    this.props.dispatch({
      type: clientsActions.LOAD_CLIENTS,
      payload:{size, page, sort, order, search}
    })
  }

  handleSearch = search => {
    const {size, page, sort, order} = this.props.clients
    this.props.dispatch({
      type: clientsActions.LOAD_CLIENTS,
      payload:{size, page, sort, order, search}
    })
  };

  onSelect = e => {
    console.log(e);
  }

  render(){
    const { getFieldDecorator } = this.props.form;
    const {loading, showErrorMessage, message, showAddModal} = this.props.purchases
    const { clients } = this.props.clients
    const options = clients.map((d,index) => <Option key={index} value={d.id}>{d.fullName}</Option>);
    return (
      <Modal
        title="Ajout un achat"
        visible={showAddModal}
        width={700}
        onOk={this.handleOk}
        onCancel={this.handleCancel}
        footer={null}
        destroyOnClose={true}
      >
        <Spin spinning={loading} tip="Loading...">
        {showErrorMessage&&<Alert message={message} type="error" banner />}
          <Form onSubmit={this.handleSubmit} className="login-form">
            <Form.Item label="Nom complet">
              {getFieldDecorator('fullName', {
                rules: [{ required: true, message: 'Nom complet Obligatoire!' }],
              })(
                <Input
                  prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                />,
              )}
            </Form.Item>
              <Form.Item label="Client">
                {getFieldDecorator(
                  'clientId',
                  {rules:[
                    { required: true, message: "Client obligatoire" },
                  ]},
                )(
                  <Select
                    showSearch
                    defaultActiveFirstOption={false}
                    showArrow={false}
                    filterOption={false}
                    onSearch={this.handleSearch}
                    onSelect={this.onSelect}
                    notFoundContent={null}
                  >
                    {options}
                  </Select>,
                )}
              </Form.Item>
            <Form.Item label="CINE">
              {getFieldDecorator('cine', {
                rules: [{ required: true, message: 'CINE Obligatoire!' }],
              })(
                <Input
                  prefix={<IdcardOutlined style={{ color: 'rgba(0,0,0,.25)' }} />}
                />,
              )}
            </Form.Item>
            <Form.Item label="Téléphone">
              {getFieldDecorator('tele', {
                rules: [
                    { required: true, message: 'Téléphone Obligatoire!' },
                    {
                      pattern:/^(?:(?:\+|00)212|0)\s*[1-9](?:[\s.-]*\d{2}){4}$/,
                      message: "Ce numéro de téléphone n'est pas valide",
                    }
                  ],
              })(
                <Input
                  prefix={<PhoneOutlined type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                />,
              )}
            </Form.Item>
            <div className={styles.footer}>
              <div>
                <Button type="primary" htmlType="submit">
                  Valider
                </Button>
              </div>
              <div>
                <Button onClick={this.handleCancel}>Annuler</Button>
              </div>
            </div>
          </Form>
        </Spin>
      </Modal>
    )
  }
}

const mapStateToProps = state=>{
  return state
}

const WrappedClientModalForm = Form.create({ name: 'add_client_modal' })(ClientModal);

export default connect(mapStateToProps)(WrappedClientModalForm)
