import React from "react"
import { connect } from "react-redux"
import { Row, Col, Button, Input } from "antd"
import clientActions from "../../../redux/clients/actions"
import styles from "./styles.module.scss"

class TopComponent extends React.Component {

  showAddModal = e =>{
    e.preventDefault()
    this.props.dispatch({
      type: clientActions.SET_STATE,
      payload:{showAddModal: true}
    })
  }

  render(){
    return(
        <Row justify="space-between" align="top" className={styles.topComponentContainer}>
          <Col md={12}>
            <Button onClick={this.showAddModal} className={styles.searchButton} type="primary">Ajouter un client</Button>
          </Col>
          <Col md={12}>
            <Input className={styles.searchInput} placeholder="Chercher" />
          </Col>
        </Row>
    )
  }
}

const mapStateToProps = state=>{
  return state.clients
}

export default connect(mapStateToProps)(TopComponent)
